if [[ $(grep -E "hivewd.*Dead" | wc -l) > 0 ]]; then screen -wipe > /var/log/hivewd.log; if [[ $(screen -ls hivewd | grep -Ec "hivewd.*Dead") > 0 ]]; then screen -wipe > /var/log/hivewd.log; fi; sleep 2; if [[ $(screen -ls hivewd | grep -c hivewd) = 0 ]]; then echo "$(date +%d.%m_%H:%M) Start HiveWD..." > /var/log/hivewd.log; screen -dmS hivewd -L -Logfile /var/log/hivewd.log bash -c 'wd run'; sleep 10; cat /var/log/hivewd.log | message success "$(date +%d.%m_%H:%M) Start WD..." payload; fi
